        def add_data_to_check_bus_info_table(self, table, query):
                table.setColumnCount(8)
                table.setHorizontalHeaderLabels(["Bus Name", "Bus ID", "Bus Type", "Driver Name", "Route Start", "Route Finish","Arrival","Departure"])
                # Fetch the data from the 'bus' table
                self.cursor.execute(query)
                bus_data = self.cursor.fetchall()
                if not bus_data:
                        self.MessageLabelOnManageBuses.setText("No Data related to the query.")
                # Set the number of rows based on the fetched data
                table.setRowCount(len(bus_data))
                # Iterate over the fetched data and populate the table
                for row, (BUS_ID, BUS_NAME, BUS_TYPE, DRIVER_ID, ROUTE_ID,Schedule_ID) in enumerate(bus_data):
                        BUS_ID_item = QtWidgets.QTableWidgetItem(str(BUS_ID))
                        BUS_NAME_item = QtWidgets.QTableWidgetItem(BUS_NAME)
                        BUS_TYPE_item = QtWidgets.QTableWidgetItem(BUS_TYPE)
                        table.setItem(row, 0, BUS_NAME_item)
                        table.setItem(row, 1, BUS_ID_item)
                        table.setItem(row, 2, BUS_TYPE_item)
                        # Fetch the driver name using DRIVER_ID
                        self.cursor.execute(f"SELECT DRIVER_NAME FROM driver WHERE DRIVER_ID = '{DRIVER_ID}'")
                        driver_data = self.cursor.fetchone()
                        if driver_data:
                                DRIVER_NAME_item = QtWidgets.QTableWidgetItem(driver_data[0])
                                table.setItem(row, 3, DRIVER_NAME_item)
                        else:
                                DRIVER_NAME_item = QtWidgets.QTableWidgetItem("None Assigned")
                                table.setItem(row, 3, DRIVER_NAME_item)
                        # Fetch the start and finish route using ROUTE_ID
                        self.cursor.execute(f"SELECT START, FINISH FROM route WHERE ROUTE_ID = '{ROUTE_ID}'")
                        route_data = self.cursor.fetchone()
                        if route_data:
                                ROUTE_START_item = QtWidgets.QTableWidgetItem(route_data[0])
                                ROUTE_FINISH_item = QtWidgets.QTableWidgetItem(route_data[1])
                                table.setItem(row, 4, ROUTE_START_item)
                                table.setItem(row, 5, ROUTE_FINISH_item)
                        else:
                                ROUTE_START_item = QtWidgets.QTableWidgetItem("None Assigned")
                                ROUTE_FINISH_item = QtWidgets.QTableWidgetItem("None Assigned")
                                table.setItem(row, 4, ROUTE_START_item)
                                table.setItem(row, 5, ROUTE_FINISH_item)
                        # Fetch the start and finish route using ROUTE_ID
                        self.cursor.execute(f"SELECT `ARRIVAL`, `DEPARTURE` FROM `schedule` WHERE `SCHEDULE_ID` =  '{Schedule_ID}'")
                        schedule_data = self.cursor.fetchone()
                        if schedule_data:
                                table.setItem(row, 6, QtWidgets.QTableWidgetItem(str(schedule_data[0])))
                                table.setItem(row, 7, QtWidgets.QTableWidgetItem(str(schedule_data[1])))
                        else:
                                table.setItem(row, 6, QtWidgets.QTableWidgetItem("None"))
                                table.setItem(row, 7, QtWidgets.QTableWidgetItem("None"))
