        def add_data_to_check_stop_info_table(self, table, query):
                self.tableWidget_10.setColumnCount(6)
                self.tableWidget_10.setHorizontalHeaderLabels(["Location ID", "Location Name", "Route ID", "Route Start", "Route Desitnation", "Fair Amount"])
                table.setColumnCount(6)
                table.setHorizontalHeaderLabels(["Location ID", "Location Name", "Route ID", "Route Start", "Route Desitnation", "Fair Amount"])
                # Fetch the data from the 'bus' table
                self.cursor.execute(query)
                stop_data = self.cursor.fetchall()
                # Set the number of rows based on the fetched data
                table.setRowCount(len(stop_data))
                if not stop_data:
                        self.MessageLabelOnManageStop.setText("No Data related to the query.")
                # Iterate over the fetched data and populate the table
                for row, (Location_ID, Location_Name, Route_ID) in enumerate(stop_data):
                        Location_ID_item = QtWidgets.QTableWidgetItem(str(Location_ID))
                        table.setItem(row, 0, Location_ID_item)
                        Location_Name_item = QtWidgets.QTableWidgetItem(str(Location_Name))
                        table.setItem(row, 1, Location_Name_item)
                        Route_ID_item = QtWidgets.QTableWidgetItem(str(Route_ID))
                        table.setItem(row, 2, Route_ID_item)
                        self.cursor.execute("SELECT `FAIR`, `START`, `FINISH` FROM `route` WHERE `ROUTE_ID` = '{0}'".format(Route_ID))
                        route_data = self.cursor.fetchone()
                        if route_data:
                                route_start_item = QtWidgets.QTableWidgetItem(str(route_data[1]))
                                table.setItem(row, 3, route_start_item)
                                route_final_item = QtWidgets.QTableWidgetItem(str(route_data[2]))
                                table.setItem(row, 4, route_final_item)
                                route_fair_item = QtWidgets.QTableWidgetItem(str(route_data[0]))
                                table.setItem(row, 5, route_fair_item)
                        else:    
                                Bus_name_item = QtWidgets.QTableWidgetItem("None")
                                table.setItem(row, 2, Bus_name_item)
